label("$0$", (0,-0.1), S);
label("$\frac{1}{2}$", (1/2,-0.1), S);
label("$1$", (1,-0.1), S);
label("$\frac{3}{2}$", (3/2,-0.1), S);
label("$2$", (2,-0.1), S);
label("$\frac{5}{2}$", (5/2,-0.1), S);
label("$3$", (3,-0.1), S);
label("$\frac{7}{2}$", (7/2,-0.1), S);
label("$4$", (4,-0.1), S);
label("$0$", (0,0), W);
label("$1$", (0,3), W);
