\begin{align*}
\angle P + \angle Q + \angle R &= 180^\circ\\
\angle P + \angle Q + \angle R &= 180^\circ - (\angle P + \angle Q)\\
\angle R &= 180^\circ - (\angle P + \angle Q)\\
\angle R &= 180^\circ - 60^\circ\\
\angle R &= \boxed{120^\circ}
\end{align*}

